angular.module("mol-species-list-templates",[]).run(["$templateCache",function($templateCache){$templateCache.put("mol-species-list-main.html",'<div ng-show=taxon class="panel panel-default"><div class=panel-heading><h2><i class="fa fa-table red"></i><strong>{{taxon.taxa | capitalize}}</strong></h2></div><div class="panel-body-table allow-scroll"><h4 ng-hide=taxon.species>There are no {{taxon.taxa}} species in the selected region.</h4><table ng-show=taxon.species class="table table-bordered table-hover table-responsive"><thead><tr><th></th><th></th></tr></thead><tbody><tr ng-repeat="species in taxon.species | orderBy:\'+sequenceid\' | orderBy:\'+common\'" ng-click=selectSpecies(species) class=clickable ng-class="{active: species.scientificname == selected}"><td><img ng-src="https://api.mol.org/imgstore/thumb?name={{species.scientificname}}" err-src=https://auth.mol.org/static/images/groups/{{taxon.class}}_40.png width=40px></td><td><b>{{species.common}}</b><br><em>{{species.scientificname}}</em></td></tr></tbody></table></div></div>')}]),angular.module("mol.species-list",["mol-species-list-templates"]).directive("molSpeciesList",[function(){return{restrict:"E",scope:{taxon:"=taxon",species:"=species"},templateUrl:"mol-species-list-main.html",controller:function($scope){$scope.$watch("taxon",function(newValue,oldValue){$scope.species=void 0},!0),$scope.selectSpecies=function(species){$scope.selected=species.scientificname,$scope.species=species}}}}]);